    //     // Otra forma
    //     setCategorias(cats => [...cats, 'HunterxHunter']);
    // }

    return (
        <div>
            <h2>GifExpertApp</h2>
            <hr />            
            <AddCategory />
            <ol>
                {
                    categorias.map( category => {
                        return <li key={ category }> { category }</li>
                    })
                }
            </ol>
            <hr/>
        </div>
    )
}
