import React ,{useState, useEffect}from 'react'
import { getGifs } from '../helpers/getGifs'

export const useFetchGifs = ( category ) => {
    
    const [state, setState] = useState({
        data: [],
        loading: true
    })

    useEffect(() => {
        getGifs( category )
            .then(imgs => {
                setTimeout(()=>{
                    useState({
                        data: imgs,
                        loading: true
                    });
                }, 3000);
            } );
    }, [category])

    setTimeout(()=>{
        setState({
            data: [],
            loading: true
        })
    },3000);
    // Para que el componente se renderize una sóla vez
    // useEffect( () =>{
    //     
    // }, [ category ]);
    return state;
}
