import React, {useState, useEffect} from 'react'
import { GifGridItem } from './GifGridItem';

export const GifGrid = ({category}) => {

    const [images, setImages] = useState([]);

    // Para que el componente se renderize una sóla vez
    useEffect( () =>{
        getGifs();
    }, []);

    const getGifs = async() => {
        const url = 'https://api.giphy.com/v1/gifs/search?api_key=HCT13FrOVsWASoEcbc6QvEwtyU3Pe14b&q=Dragon+Ball&limit=10';
        const resp = await fetch(url);
        const { data } = await resp.json();

        const gifs = data.map( img => {
            return{
                id:img.id,
                title:img.title,
                url:img.images?.downsized_medium.url
            }
        })
        console.log(gifs);
        setImages(gifs);
    }
    
    return (
        <div>
            {/* <li key={ category }> { category }</li> */}
            <h3>{ category }</h3>
            {/* <o>
                {
                    images.map(({id, title}) =>(
                        <li key={ id } >{ title }</li>
                    ))

                }
            </o> */}
            {
                    images.map((img) =>(
                        <GifGridItem 
                            key = { img.id }
                            img = { img }
                        />
                    ))

                }
        </div>
    )
}
