import React , { useState } from 'react'

export const AddCategory = () => {
    const [inputValue, setInputValue] = useState('Hola Mundo');
    const hadleInputChange = ( e )=>{
        setInputValue(e.target.value)
    }
    return (
        <>
        <form>
            <h1>{inputValue}</h1>
            <h2>Add Categoria</h2>
            <input 
                type="text"
                value={inputValue}
                onChange={hadleInputChange}
            />
        </form>    
        </>
    )
}
